import { NavLink as Link } from 'react-router-dom';
import styled from 'styled-components';

export const Nav = styled.nav`
  font-family: "Medium";
  background: #555555;
  height: 85px;
  display: flex;
  justify-content: space-between;
  border-width: 2px; 
  border-color: #bbbbbb;
  border-style: solid;
`;

export const NavLink = styled(Link)`
  color: #000000;
  display: flex;
  align-items: center;
  text-decoration: none;
  padding: 0 1rem;
  height: 50%;
  cursor: pointer;
  &.active {
    color: #8BB8E6;
  }
`;

export const NavTitle = styled.div`
 color: #8BB8E6;
 line-height:10px;
 font-size: xx-large;
 position: relative;
 padding-left: 10px;
 justify-content: left;
 text-align: left;
 vertical-align: center;
`

export const NavMenu = styled.div`
  display: flex;
  position: relative;
  justifyContent: center;
  align-items: center;
  align-text: center;
`;

export const NavBtnContain = styled.div`
  display: flex;
  align-items: right;
  align-text: center;
`;

export const NavBtn = styled.nav`
  display: flex;
  align-items: center;
  align-text: center;
  justify-content: center;
  margin-right: 10px;
  /* justify-content: flex-end;
  width: 100vw; */
  @media screen and (max-width: 768px) {
    display: none;
  }
`;

export const NavBtnLink = styled(Link)`
  border-radius: 4px;
  width: auto;
  min-width: 6vh;
  align-text: center;
  align-items: center;
  justifyContent: center;
  background: #DDDDDD;
  padding: 10px 20px;
  color: #000000;
  outline: none;
  border: none;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  text-decoration: none;
  /* Second Nav */
  &:hover {
    transition: all 0.2s ease-in-out;
    background: #BBBBBB;
    color: #000000;
  }
`;
